mapper.update:
update works
set end_time = sysdate
where work_no = ${workNo}

mapper.select:
select end_time
from works
where work_no = ${workNo}

select e.emp_name, e.emp_position, w.start_time, w.end_time
from works w
join employees e
on e.employee_no = w.employee_no
where to_char(start_time, 'YYYY-MM-DD') = #{choiceDay}
and e.company_id = #{companyId}
